package aula_2_;

public class Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n1 = 2, n2 = 2;
		
		System.out.println("Vari치vel n1:" + n1);
		System.out.println("Vari치vel n1:" + (++ n1));
		
		System.out.println("Vari치vel n2:" + n2);	
		System.out.println("Vari치vel n2:" + ( n2 ++ ));
	}

}
